    private void writeResponse(OutputStream output, ServerResponse response) throws IOException {

        Map<String, String> headers = response.getHeaders();

        output.write(("HTTP/1.1 " + response.getStatus() + "\r\n").getBytes());
        for(Map.Entry entry : headers.entrySet()) {
            output.write((entry.getKey() + ": " + entry.getValue() + "\r\n").getBytes());
        }

        //output.write(("Content-length: " + response.getBody().length() + "\r\n").getBytes());
        output.write("\r\n".getBytes());
        output.write(response.getBody().getBytes());
        output.flush();

        //output.write(("X-Server-Name: eksamensserver" + "\r\n").getBytes());
        //output.write("Connection: close\r\n".getBytes());
        //output.write(("Content-Type: " + headers.get("Content-") + "\r\n").getBytes());
        //output.write(("Location: " + location + "\r\n").getBytes());
    }

    private void parseUri(String statusLine) throws UnsupportedEncodingException {
        System.out.println(statusLine.split(" ")[1]);
        Uri uri = new Uri(statusLine.split(" ")[1]);

        System.out.println(uri);
        System.out.println(uri.getQuery().getArguments());
        //metode (GET, POST): statusLine.split( " ")[0];
        path = uri.getPath();
        parameters = uri.getQuery().getArguments();
    }


    // Todo: separeres ut?
    private static String readNextLine(InputStream input) throws IOException {
        StringBuilder response = new StringBuilder();
        int c;

        while ((c = input.read()) != -1) {
            if (c == '\r') {
                input.mark(1);
                c = input.read();
                if (c != '\n') {
                    input.reset();
                }
                break;
            }
            response.append((char)c);
        }
        return response.toString();
    }

    public int getPort() {
        return actualPort;
    }


